import React, { useState, useEffect } from "react";
import pokeHeader from "../resources/pokemon.png";
import Container from "@material-ui/core/Container";
import styled from "styled-components";

const WrapperCards = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Wrapper = styled.div`
  width: 100%;
`;

const PokeWrapper = styled.div`
  display: flex;
  flex-flow: column;
  margin: 20px;
  height: 300px;
  width: 220px;
  border: 5px solid #f9c75e;
  border-radius: 5px;
  /* box-shadow: 0px 0px 5px 0px #797c7f; */
`;

const PokeImage = styled.img`
  width: 82%;
  display: block;
  margin: 15px auto;
  border: 2px solid #f9c75e;
`;

const PokemonName = styled.h2`
  position: relative;
  top: 14px;
  left: 20px;
  margin: 0;
  font-size: 18px;
  font-weight: bold;
  text-transform: capitalize;
`;
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 400px;
  width: 100%;
  background-size: cover;
  background-image: url(${pokeHeader});
`;

const Input = styled.input`
  font-size: 1em;
  height: 40px;
  width: 400px;
  border-radius: 5px;
  border: none;
  &:focus {
    outline-color: #ade7f5;
  }
`;

const InfoContaier = styled.div`
  display: flex;
  justify-content: center;
  flex-flow: row;
  width: 100%;
`;

const PokemonType = styled.div`
  margin: 3px;
`;

//paused for now
// function Header({ handleChange, pokemon }) {
//   return (
//     <SearchContainer>
//       <Input type="text" placeholder="Search for pokemon" />
//     </SearchContainer>
//   );
// }

class PokemonCard extends React.Component {
  render() {
    const pokemonInfo = this.props;
    return (
      <PokeWrapper>
        <PokemonName>{pokemonInfo.name}</PokemonName>
        {pokemonInfo.stats.map((stat, i) => (
          <div>{stat.stat}</div>
        ))}
        <PokeImage src={pokemonInfo.sprites.front_default} />
        <InfoContaier>
          {pokemonInfo.types.map((type, i) => (
            <PokemonType key={i}>{type.type.name}</PokemonType>
          ))}
        </InfoContaier>
      </PokeWrapper>
    );
  }
}

function PokeList() {
  const [pokeInfo, setPokeInfo] = useState([]);
  const initialURL = `https://pokeapi.co/api/v2/pokemon/`;

  function getPokemon({ url }) {
    return new Promise((resolve, reject) => {
      fetch(url)
        .then((res) => res.json())
        .then((data) => {
          resolve(data);
        });
    });
  }

  async function getAllPokemon(url) {
    return new Promise((resolve, reject) => {
      fetch(url)
        .then((res) => res.json())
        .then((data) => {
          resolve(data);
        });
    });
  }

  const loadPokemon = async (data) => {
    // if (data.length > 0) {
    let _pokemonData = await Promise.all(
      data.map(async (pokemon) => {
        let pokemonRecord = await getPokemon(pokemon);
        return pokemonRecord;
      })
    );
    setPokeInfo(_pokemonData);
    // }
  };

  useEffect(() => {
    async function fetchData() {
      let response = await getAllPokemon(initialURL);
      console.log(response, "data from useeffect");
      await loadPokemon(response.results);
    }
    fetchData();
  }, []);

  return (
    <Wrapper>
      <WrapperCards>
        {console.log(pokeInfo)}
        {pokeInfo.map((pokemon, i) => {
          return <PokemonCard key={i} {...pokemon} />;
        })}
      </WrapperCards>
    </Wrapper>
  );
}

const EinoPage = () => {
  function handleChange(e) {
    // setPokeName(e.target.value);
    // console.log(e.target.value, "value from handlechange");
  }

  return (
    <div>
      <Container fixed>
        {/* <Header handleChange={handleChange} /> */}
        <PokeList />
      </Container>
    </div>
  );
};

export default EinoPage;
