import React, { useState, useEffect } from "react";
import styled from "styled-components";

const WrapperCards = styled.div`
  display: flex;
  /* flex-flow: row; */
  flex-wrap: wrap;
`;

const Wrapper = styled.div`
  width: 100%;
`;

const PokeWrapper = styled.div`
  display: flex;
  flex-flow: column;
  margin: 20px;
  height: 300px;
  width: 150px;
  background-color: blue;
`;

function PokeList() {
  const [pokeInfo, setPokeInfo] = useState([]);
  const initialURL = "https://pokeapi.co/api/v2/pokemon";

  //   async function fetchData() {
  //     const res = await fetch("https://pokeapi.co/api/v2/pokemon/");
  //     // res.json().then((res) => setPokeInfo(res));
  //     res.json().then((res) => loadPokemon(res.result));
  //     // return;
  //   }

  //   async function fetchPokeData(url) {
  //     return new Promise((resolve, reject) => {
  //       fetch(url)
  //         .then((res) => res.json())
  //         .then((data) => {
  //           resolve(data);
  //         });
  //     });
  //   }

  function getPokemon({ url }) {
    return new Promise((resolve, reject) => {
      fetch(url)
        .then((res) => res.json())
        .then((data) => {
          resolve(data);
        });
    });
  }

  async function getAllPokemon(url) {
    return new Promise((resolve, reject) => {
      fetch(url)
        .then((res) => res.json())
        .then((data) => {
          resolve(data);
        });
    });
  }

  const loadPokemon = async (data) => {
    let _pokemonData = await Promise.all(
      data.map(async (pokemon) => {
        let pokemonRecord = await getPokemon(pokemon);
        return pokemonRecord;
      })
    );
    setPokeInfo(_pokemonData);
  };

  //   useEffect(() => {
  //     getPokemon();
  //   }, []);
  useEffect(() => {
    async function fetchData() {
      let response = await getAllPokemon(initialURL);
      await loadPokemon(response.results);
      //   setLoading(false);
    }
    fetchData();
  }, []);

  return (
    <Wrapper>
      <WrapperCards>
        {console.log(pokeInfo)}
        {pokeInfo.map((pokemon, i) => {
          return <PokemonCard key={i} {...pokemon} />;
        })}
      </WrapperCards>
    </Wrapper>
  );
}

// {pokeInfo &&
//     pokeInfo.results.map((poke, i) => <PokemonCard key={i} {...poke} />)}

class PokemonCard extends React.Component {
  render() {
    const pokemonInfo = this.props;
    return (
      <PokeWrapper>
        <img src={pokemonInfo.sprites.front_default} />
        <div className="info-wrapper">
          <div>{pokemonInfo.name}</div>
        </div>
      </PokeWrapper>
    );
  }
}

const EinoPage = () => <PokeList />;

export default EinoPage;
