import React, { useState, useEffect } from "react";
import styled from "styled-components";

const WrapperCards = styled.div`
  display: flex;
  flex-flow: row;
`;

const Wrapper = styled.div`
  width: 100%;
`;

const PokeWrapper = styled.div`
  display: flex;
  flex-flow: row;
  margin: 20px;
  height: 300px;
  width: 200px;
  background-color: blue;
`;

function PokeList() {
  const [pokeInfo, setPokeInfo] = useState();

  async function fetchData() {
    const res = await fetch("https://pokeapi.co/api/v2/pokemon/");
    res.json().then((res) => setPokeInfo(res));
    // return;
  }

  async function fetchPokeData(url) {
    return new Promise((resolve, reject) => {
      fetch(url)
        .then((res) => res.json())
        .then((data) => {
          resolve(data);
        });
    });
  }

  const LoadingPokemons = async (data) => {
    let _pokemon = await Promise.all(
      data.map(async (pokemon) => {
        let pokemonRecord = await fetchPokeData(pokemon.url);
        return pokemonRecord;
      })
    );

    setPokeInfo(_pokemon);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Wrapper>
      <WrapperCards>
        {console.log(pokeInfo)}
        {pokeInfo &&
          pokeInfo.results.map((poke, i) => <PokemonCard key={i} {...poke} />)}
      </WrapperCards>
    </Wrapper>
  );
}

class PokemonCard extends React.Component {
  render() {
    const pokemonInfo = this.props;
    return (
      <PokeWrapper>
        <img />
        <div className="info-wrapper">
          <div>{pokemonInfo.name}</div>
        </div>
      </PokeWrapper>
    );
  }
}

const EinoPage = () => <PokeList />;

export default EinoPage;
